<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\TenantTermsOfUseRef;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\Create\TenantTermsOfUseCreateUsecase;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\List\TenantTermsOfUseListUsecase;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\Update\TenantTermsOfUseUpdateUsecase;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\Delete\TenantTermsOfUseDeleteUsecase;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\Retrieve\TenantTermsOfUseRetrieveUsecase;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\Enable\TenantTermsOfUseEnableUsecase;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\Disable\TenantTermsOfUseDisableUsecase;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantTermsOfUseAllowController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantTermsOfUseCreateUsecase $createUsecase,
        private readonly TenantTermsOfUseListUsecase $listUsecase,
        private readonly TenantTermsOfUseUpdateUsecase $updateUsecase,
        private readonly TenantTermsOfUseDeleteUsecase $deleteUsecase,
        private readonly TenantTermsOfUseRetrieveUsecase $retrieveUsecase,
        private readonly TenantTermsOfUseEnableUsecase $enableUsecase,
        private readonly TenantTermsOfUseDisableUsecase $disableUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/me/acl/access/tenants-terms-of-use",
        tags: ["Tenant terms of use access control"],
        description:"Allows",
        responses: [
        new OA\Response(response: 200, description: "Ok"),
    ]
    )
    ]
    public function genericAllow(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check generic allow for Tenant terms of use");
        $span = $this->startSpan("Generic allow for Tenant terms of use");
        try {
            $value = [
              'allows' => [
                $this->createUsecase->allowCreate(),
                $this->listUsecase->allowList(),
                $this->updateUsecase->allowUpdate(),
              $this->deleteUsecase->allowDelete(),
              $this->retrieveUsecase->allowRetrieve(),
              $this->enableUsecase->allowEnable(),
              $this->disableUsecase->allowDisable(),
              ],
              'fields' => [
                'noEditables' => $this->updateUsecase->fixedFields(),
                'noVisibles' => $this->retrieveUsecase->hiddenFields()
              ]
            ];
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[OA\Get(
        path: "/api/me/acl/access/tenants-terms-of-use/{uid}",
        tags: ["Tenant terms of use access control"],
        description:"Allows",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok"),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function contextualAllow(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check specific allow for Tenant terms of use");
        $span = $this->startSpan("Specific allow for Tenant terms of use");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $ref = new TenantTermsOfUseRef(uid: $args['uid']);
            $value = [
              'allows' => [
                $this->updateUsecase->allowUpdate($ref),
              $this->deleteUsecase->allowDelete($ref),
              $this->retrieveUsecase->allowRetrieve($ref),
              $this->enableUsecase->allowEnable($ref),
              $this->disableUsecase->allowDisable($ref),
              ],
              'fields' => [
                'noEditables' => $this->updateUsecase->fixedFields($ref),
                'noVisibles' => $this->retrieveUsecase->hiddenFields($ref)
              ]
            ];
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
