<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use DateTime;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\Create\TenantTermsOfUseCreateUsecase;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseUidVO;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseTenantVO;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseTextVO;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseAttachedVO;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseActivationDateVO;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\Create\TenantTermsOfUseCreateParams;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Application\Usecase\Create\TenantTermsOfUseCreateResult;

class TenantTermsOfUseCreateController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly TenantTermsOfUseCreateUsecase $createUsecase,
        private readonly Context $context,
    ) {
    }
    #[OA\Post(
        path: "/api/access/tenants-terms-of-use",
        tags: ["Tenant terms of use"],
        description:"Create",
        requestBody: new OA\RequestBody(content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantTermsOfUseApiDTO")),
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantTermsOfUseApiDTO")),
              new OA\Response(response: 422, description: "Constraint error"),
          ]
    )]
    public function create(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Create Tenant terms of use");
        $span = $this->startSpan("Create Tenant terms of use");
        $this->sql->begin();
        try {
            $value = $this->readTenantTermsOfUse($request);
            $result = $this->createUsecase->create($value);
            $this->sql->commit();
            $value = $this->mapTenantTermsOfUse($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(201)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }

    private function readTenantTermsOfUse(ServerRequestInterface $request): TenantTermsOfUseCreateParams
    {
        $this->logDebug("Read entity for Tenant terms of use");
        $span = $this->startSpan("Read entity for Tenant terms of use");
        try {
            $body = $request->getParsedBody();
            $errorsList = new ConstraintFailList();
            $value = new TenantTermsOfUseCreateParams();
            $value->uid(TenantTermsOfUseUidVO::tryFrom($body['uid'] ?? null, $errorsList));
            if (in_array('tenant', array_keys($body))) {
                $value->tenant(TenantTermsOfUseTenantVO::tryFrom(isset($body['tenant']['$ref']) ? new TenantRef($body['tenant']['$ref']) : null, $errorsList));
            }
            $value->text(TenantTermsOfUseTextVO::tryFrom($body['text'] ?? null, $errorsList));
            $attached = $body['attached'] ?? null;
            $preffixAttached = $this->context->getBaseUrl() . '/api/access/tenants-terms-of-use/-/temp-attached?temp=';
            if ($attached && strpos($attached, $preffixAttached) === 0) {
                $value->attached(TenantTermsOfUseAttachedVO::tryFromTemporal(base64_decode(substr($attached, strlen($preffixAttached))), $errorsList));
            }
            $value->activationDate(TenantTermsOfUseActivationDateVO::tryFrom($body['activationDate'] ?? null, $errorsList));
            $value->version(TenantTermsOfUseVersionVO::tryFrom($body['version'] ?? null, $errorsList));
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $value;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function mapTenantTermsOfUse(TenantTermsOfUseCreateResult $value): TenantTermsOfUseApiDTO
    {
        $this->logDebug("Map entity to output dto for Tenant terms of use");
        $span = $this->startSpan("Map entity to output dto for Tenant terms of use");
        try {
            $tenant = $value->getTenant();
            $dto = new TenantTermsOfUseApiDTO();
            $dto->uid = $value->getUid();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->text = $value->getText();
            $dto->enabled = $value->isEnabled();
            if ($value->getAttached()) {
                $dto->attached = $this->context->getBaseUrl() . '/api/access/tenants-terms-of-use/' . $value->getUid() . '/attached';
            }
            $dto->activationDate = $value->getActivationDate()?->format(DateTime::ATOM);
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
