<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\Event;

use Override;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\TrustedClient;
use Civi\Lughauth\Shared\Event\PublicEvent;

class TrustedClientUpdateEvent extends TrustedClientEvent implements PublicEvent
{
    public function __construct(
        TrustedClient $payload,
        public readonly TrustedClient $original
    ) {
        parent::__construct($payload);
    }
    #[Override]
    public function eventType(): string
    {
        return 'trusted-client.update';
    }
    #[Override]
    public function schemaVersion(): string
    {
        return 'v1';
    }
    #[Override]
    public function payload(): array
    {
        return $this->payload->asPublicJson();
    }
    #[Override]
    public function original(): array
    {
        return $this->original->asPublicJson();
    }
}
