<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\Gateway;

class TrustedClientFilter
{
    public function __construct(
        private ?array $uids = null,
        private ?string $search = null,
        public readonly ?string $code = null,
    ) {
    }
    public function uids(): ?array
    {
        return $this->uids;
    }
    public function withUids(array $uids): self
    {
        $copy = clone $this;
        $copy->uids = $uids;
        return $copy;
    }
    public function search(): ?string
    {
        return $this->search;
    }
    public function withSearch(string $search): self
    {
        $copy = clone $this;
        $copy->search = $search;
        return $copy;
    }
    public function code(): ?string
    {
        return $this->code;
    }
    public function withCode($code): self
    {
        $copy = clone $this;
        $copy->code = $code;
        return $copy;
    }
}
