<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientCodeVO;

trait TrustedClientCodeAccesor
{
    private TrustedClientCodeVO $_code;
    public function getCode(): string
    {
        return $this->_code->value();
    }
    public function code(): TrustedClientCodeVO
    {
        return $this->_code;
    }
    public function isCodeChanged(?self $original = null): bool
    {
        return $this->_code->value() !== ($original ? $original->_code->value() : null);
    }
    public function withCode(TrustedClientCodeVO|string $code): self
    {
        $copy = clone $this;
        $copy->_code = TrustedClientCodeVO::from($code);
        return $copy;
    }
}
