<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TrustedClient\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Application\Usecase\Create\TrustedClientCreateUsecase;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientUidVO;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientCodeVO;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientPublicAllowVO;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientSecretOauthVO;
use Civi\Lughauth\Shared\Security\AesCypherService;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsVO;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsUidVO;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsUrlVO;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsVersionVO;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsItem;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsListRef;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\ValueObject\TrustedClientVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Application\Usecase\Create\TrustedClientCreateParams;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Application\Usecase\Create\TrustedClientCreateResult;

class TrustedClientCreateController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly TrustedClientCreateUsecase $createUsecase,
        private readonly AesCypherService $cypherService,
    ) {
    }
    #[OA\Post(
        path: "/api/access/trusted-clients",
        tags: ["Trusted client"],
        description:"Create",
        requestBody: new OA\RequestBody(content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TrustedClientApiDTO")),
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TrustedClientApiDTO")),
              new OA\Response(response: 422, description: "Constraint error"),
          ]
    )]
    public function create(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Create Trusted client");
        $span = $this->startSpan("Create Trusted client");
        $this->sql->begin();
        try {
            $value = $this->readTrustedClient($request);
            $result = $this->createUsecase->create($value);
            $this->sql->commit();
            $value = $this->mapTrustedClient($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(201)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }

    private function readTrustedClient(ServerRequestInterface $request): TrustedClientCreateParams
    {
        $this->logDebug("Read entity for Trusted client");
        $span = $this->startSpan("Read entity for Trusted client");
        try {
            $body = $request->getParsedBody();
            $errorsList = new ConstraintFailList();
            $value = new TrustedClientCreateParams();
            $value->uid(TrustedClientUidVO::tryFrom($body['uid'] ?? null, $errorsList));
            $value->code(TrustedClientCodeVO::tryFrom($body['code'] ?? null, $errorsList));
            $value->publicAllow(TrustedClientPublicAllowVO::tryFrom($body['publicAllow'] ?? null, $errorsList));
            $readSecretOauth = $body['secretOauth'] ?? '******';
            if ($readSecretOauth && '******' !== $readSecretOauth) {
                $value->secretOauth(TrustedClientSecretOauthVO::tryFromPlainText($this->cypherService, $readSecretOauth, $errorsList));
            }
            $allowedRedirectsList = [];
            if (in_array('allowedRedirects', array_keys($body))) {
                $allowedRedirects = $body['allowedRedirects'] ?? null;
                if (is_array($allowedRedirects)) {
                    foreach ($allowedRedirects as $item) {
                        $innerErrorsList = new ConstraintFailList();
                        $innerUid = TrustedClientAllowedRedirectsUidVO::tryFrom($item['uid'] ? ''.$item['uid'] : null, $innerErrorsList);
                        $innerUrl = TrustedClientAllowedRedirectsUrlVO::tryFrom($item['url'] ? ''.$item['url'] : null, $innerErrorsList);
                        $innerVersion = TrustedClientAllowedRedirectsVersionVO::tryFrom($item['version'] ? $item['version'] : null, $innerErrorsList);
                        if (!$innerErrorsList->hasErrors()) {
                            $allowedRedirectsList[] = new TrustedClientAllowedRedirectsItem(
                                uid: $innerUid,
                                url: $innerUrl,
                                version: $innerVersion,
                            );
                        } else {
                            $errorsList->add($innerErrorsList);
                        }
                    }
                } else {
                    $errorsList->add(new ConstraintFail('not-array', ['allowedRedirects'], $allowedRedirects, ['array']));
                }
            }
            $value->allowedRedirects(TrustedClientAllowedRedirectsVO::from(TrustedClientAllowedRedirectsListRef::fromArray($allowedRedirectsList)));
            $value->version(TrustedClientVersionVO::tryFrom($body['version'] ?? null, $errorsList));
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $value;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function mapTrustedClient(TrustedClientCreateResult $value): TrustedClientApiDTO
    {
        $this->logDebug("Map entity to output dto for Trusted client");
        $span = $this->startSpan("Map entity to output dto for Trusted client");
        try {
            $dto = new TrustedClientApiDTO();
            $dto->uid = $value->getUid();
            $dto->code = $value->getCode();
            $dto->publicAllow = $value->isPublicAllow();
            $dto->secretOauth = '******';
            $dto->enabled = $value->isEnabled();
            $allowedRedirects = [];
            foreach ($value->getAllowedRedirects() as $item) {
                $allowedRedirects[] = [
                  'uid' => $item->uid(),
                  'url' => $item->getUrl(),
                  'version' => $item->getVersion(),
                 ];
            }
            $dto->allowedRedirects = $allowedRedirects;
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
