<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\TrustedClient\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Application\Usecase\Retrieve\TrustedClientRetrieveUsecase;
use Civi\Lughauth\Shared\Security\AesCypherService;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Application\Usecase\Retrieve\TrustedClientRetrieveResult;

class TrustedClientRetrieveController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TrustedClientRetrieveUsecase $retrieveUsecase,
        private readonly AesCypherService $cypherService,
    ) {
    }
    #[OA\Get(
        path: "/api/access/trusted-clients{uid}",
        tags: ["Trusted client"],
        description:"Create",
        parameters: [
               new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
          ],
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TrustedClientApiDTO")),
              new OA\Response(response: 404, description: "Not found"),
          ]
    )]
    public function retrieve(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve Trusted client");
        $span = $this->startSpan("Retrieve Trusted client");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->retrieveUsecase->retrieve($uid);
            $value = $this->mapTrustedClient($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapTrustedClient(TrustedClientRetrieveResult $value): TrustedClientApiDTO
    {
        $this->logDebug("Map entity to output dto for Trusted client");
        $span = $this->startSpan("Map entity to output dto for Trusted client");
        try {
            $dto = new TrustedClientApiDTO();
            $dto->uid = $value->getUid();
            $dto->code = $value->getCode();
            $dto->publicAllow = $value->isPublicAllow();
            $dto->secretOauth = '******';
            $dto->enabled = $value->isEnabled();
            $allowedRedirects = [];
            foreach ($value->getAllowedRedirects() as $item) {
                $allowedRedirects[] = [
                  'uid' => $item->uid(),
                  'url' => $item->getUrl(),
                  'version' => $item->getVersion(),
                 ];
            }
            $dto->allowedRedirects = $allowedRedirects;
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
