<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Application\Policy\Allow\Accept;

use Throwable;
use Civi\Lughauth\Features\Access\Master\User\Application\Usecase\Accept\UserAcceptAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class IsAutenticatedAcceptAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(UserAcceptAllowDecision $proposal): UserAcceptAllowDecision
    {
        $this->logDebug("Check IsAutenticatedAcceptAllow User");
        $span = $this->startSpan("Check IsAutenticatedAcceptAllow User");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->isAllowed()) {
                if (! !$userContext->anonimous) {
                    $proposal->deny('Disabled if not IsAutenticatedAccept');
                }
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
