<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Master\User\Domain\UserRef;
use Civi\Lughauth\Features\Access\Master\User\Application\Service\Visibility\UserVisibilityService;
use Civi\Lughauth\Features\Access\Master\User\Domain\Gateway\UserReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserVisibilityService $visibility,
        private readonly UserReadGateway $reader
    ) {
    }

    public function allowRetrieve(?UserRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for User");
        $span = $this->startSpan("Check allow of retrieve usecase for User");
        try {
            $result = $this->dispacher->dispatch(new UserRetrieveAllowDecision(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?UserRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for User");
        $span = $this->startSpan("Check hidden fields usecase for User");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): UserRetrieveResult
    {
        $this->logDebug("Run retrieve usecase for User");
        $span = $this->startSpan("Run retrieve usecase for User");
        try {
            $ref = new UserRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new UserRetrieveResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
