<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Master\User\Domain\UserRef;
use Civi\Lughauth\Features\Access\Master\User\Application\Service\Visibility\UserVisibilityService;
use Civi\Lughauth\Features\Access\Master\User\Domain\Gateway\UserWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserVisibilityService $visibility,
        private readonly UserWriteGateway $writer
    ) {
    }

    public function allowUpdate(?UserRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for User");
        $span = $this->startSpan("Check allow update usecase for User");
        try {
            $result = $this->dispacher->dispatch(new UserUpdateAllowDecision(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?UserRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for User");
        $span = $this->startSpan("Check fixed fields for update usecase for User");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, UserUpdateParams $params): UserUpdateResult
    {
        $this->logDebug("Run update usecase for User");
        $span = $this->startSpan("Run update usecase for User");
        try {
            $ref = new UserRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new UserUpdateCheck($params, $original));
            $enriched = $this->dispacher->dispatch(new UserUpdateEnrich($params, $original, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $modified = $original->update($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new UserUpdateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
