<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Domain\Event;

use Override;
use Civi\Lughauth\Features\Access\Master\User\Domain\User;
use Civi\Lughauth\Shared\Event\PublicEvent;

class UserUpdateEvent extends UserEvent implements PublicEvent
{
    public function __construct(
        User $payload,
        public readonly User $original
    ) {
        parent::__construct($payload);
    }
    #[Override]
    public function eventType(): string
    {
        return 'user.update';
    }
    #[Override]
    public function schemaVersion(): string
    {
        return 'v1';
    }
    #[Override]
    public function payload(): array
    {
        return $this->payload->asPublicJson();
    }
    #[Override]
    public function original(): array
    {
        return $this->original->asPublicJson();
    }
}
