<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserEnabledVO;

trait UserEnabledAccesor
{
    private ?UserEnabledVO $_enabled = null;
    public function isEnabled(): ?bool
    {
        return $this->_enabled?->value();
    }
    public function enabled(): ?UserEnabledVO
    {
        return $this->_enabled;
    }
    public function isEnabledChanged(?self $original = null): bool
    {
        return $this->_enabled->value() !== ($original ? $original->_enabled->value() : null);
    }
    public function withEnabled(UserEnabledVO|bool|null $enabled): self
    {
        $copy = clone $this;
        $copy->_enabled = UserEnabledVO::from($enabled);
        return $copy;
    }
}
