<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserPasswordVO;
use Civi\Lughauth\Shared\Security\AesCypherService;

trait UserPasswordAccesor
{
    private UserPasswordVO $_password;
    public function getPassword(): string
    {
        return $this->_password->value();
    }
    public function password(): UserPasswordVO
    {
        return $this->_password;
    }
    public function getCypheredPassword(AesCypherService $cypher): string
    {
        return $this->_password->cypheredValueWith($cypher);
    }
    public function getPlainPassword(AesCypherService $cypher): string
    {
        return $this->_password->plainValueWith($cypher);
    }
    public function isPasswordChanged(?self $original = null): bool
    {
        return $this->_password->value() !== ($original ? $original->_password->value() : null);
    }
    public function withPassword(UserPasswordVO|string $password): self
    {
        $copy = clone $this;
        $copy->_password = UserPasswordVO::from($password);
        return $copy;
    }
}
