<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserProviderVO;

trait UserProviderAccesor
{
    private ?UserProviderVO $_provider = null;
    public function getProvider(): ?string
    {
        return $this->_provider?->value();
    }
    public function provider(): ?UserProviderVO
    {
        return $this->_provider;
    }
    public function isProviderChanged(?self $original = null): bool
    {
        return $this->_provider->value() !== ($original ? $original->_provider->value() : null);
    }
    public function withProvider(UserProviderVO|string|null $provider): self
    {
        $copy = clone $this;
        $copy->_provider = UserProviderVO::from($provider);
        return $copy;
    }
}
