<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\Holder;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserNameVO;

trait UserNameAttributeHolder
{
    protected UserNameVO|string|null $name = null;
    protected bool $nameAssigned = false;

    public function getNameOrDefault(?UserNameVO $name): ?UserNameVO
    {
        return $this->nameAssigned ? ($this->name !== null ? UserNameVO::from($this->name) : null) : $name;
    }
    public function name(UserNameVO|string|null $name): static
    {
        $this->name = $name;
        $this->nameAssigned = true;
        return $this;
    }
    public function getName(): ?string
    {
        return is_a($this->name, UserNameVO::class) ? $this->name->value() : $this->name;
    }
    public function unsetName(): static
    {
        $this->name = null;
        $this->nameAssigned = false;
        return $this;
    }
    protected function withDefaultName(): void
    {
    }
    protected function withAssertedNameRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->nameAssigned = $this->nameAssigned) {
            $value->name = UserNameVO::tryFrom($this->name, $errorsList);
        }
    }
}
