<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\Holder;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserTenantVO;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;

trait UserTenantAttributeHolder
{
    protected UserTenantVO|TenantRef|null $tenant = null;
    protected bool $tenantAssigned = false;

    public function getTenantOrDefault(?UserTenantVO $tenant): ?UserTenantVO
    {
        return $this->tenantAssigned ? ($this->tenant !== null ? UserTenantVO::from($this->tenant) : null) : $tenant;
    }
    public function tenant(UserTenantVO|TenantRef|null $tenant): static
    {
        $this->tenant = $tenant;
        $this->tenantAssigned = true;
        return $this;
    }
    public function getTenant(): ?TenantRef
    {
        return is_a($this->tenant, UserTenantVO::class) ? $this->tenant->value() : $this->tenant;
    }
    public function unsetTenant(): static
    {
        $this->tenant = null;
        $this->tenantAssigned = false;
        return $this;
    }
    protected function withDefaultTenant(): void
    {
    }
    protected function withAssertedTenantRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->tenantAssigned = $this->tenantAssigned) {
            $value->tenant = UserTenantVO::tryFrom($this->tenant, $errorsList);
        }
    }
}
