<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;
use Civi\Lughauth\Features\Access\Master\User\Domain\UserApproveOptions;

class UserApproveVO
{
    public static function fromString(string $value): UserApproveVO
    {
        $errorsList = new ConstraintFailList();
        $candidate = self::tryFrom($value, $errorsList);
        if ($errorsList->hasErrors()) {
            throw $errorsList->asConstraintException();
        }
        return $candidate;
    }
    public static function empty(): UserApproveVO
    {
        return new UserApproveVO(null);
    }
    public static function from(UserApproveVO|UserApproveOptions|null $value): UserApproveVO
    {
        if (is_a($value, UserApproveVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            $candidate = self::tryFrom($value, $errorsList);
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $candidate;
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?UserApproveVO
    {
        if (is_a($value, UserApproveVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new UserApproveVO($value);
        } elseif (is_string($value)) {
            if ($typed = self::readFromString($value)) {
                return self::tryFrom($typed, $list);
            } else {
                $list->add(new ConstraintFail('wrong_format', ['approve'], [$value], ['UserApproveOptions']));
                return null;
            }
        } elseif (is_a($value, UserApproveOptions::class)) {
            return new UserApproveVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['approve'], [$value], ['UserApproveOptions']));
            return null;
        }
    }
    private static function readFromString(string $enumString): ?UserApproveOptions
    {
        if (in_array($enumString, ['uNVERIFIED', 'UNVERIFIED', 'unverified'])) {
            return UserApproveOptions::UNVERIFIED;
        }
        if (in_array($enumString, ['pENDING', 'pending', 'PENDING'])) {
            return UserApproveOptions::PENDING;
        }
        if (in_array($enumString, ['ACCEPTED', 'accepted', 'aCCEPTED'])) {
            return UserApproveOptions::ACCEPTED;
        }
        if (in_array($enumString, ['rejected', 'rEJECTED', 'REJECTED'])) {
            return UserApproveOptions::REJECTED;
        }
        return null;
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?UserApproveOptions $approve
    ) {
    }
    public function value(): ?UserApproveOptions
    {
        return $this->approve;
    }
}
