<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Master\User\Domain\Gateway\UserWriteRepository;
use Civi\Lughauth\Features\Access\Master\User\Infrastructure\Connector\Pdo\UserPdoConnector;
use Civi\Lughauth\Features\Access\Master\User\Domain\Gateway\UserSlide;
use Civi\Lughauth\Features\Access\Master\User\Domain\Gateway\UserCursor;
use Civi\Lughauth\Features\Access\Master\User\Domain\Gateway\UserFilter;
use Civi\Lughauth\Features\Access\Master\User\Domain\UserRef;
use Civi\Lughauth\Features\Access\Master\User\Domain\User;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\EntityChangelog\EntityChangelogService;

class UserWriteRepositoryAdapter implements UserWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly UserPdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
        private readonly EntityChangelogService $changelog,
    ) {
    }
    #[Override]
    public function resolveForUpdate(UserRef $ref): ?User
    {
        return $this->conn->retrieveForUpdate(new UserFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?UserFilter $filter = null, ?UserCursor $sort = null): UserSlide
    {
        $this->logDebug("Count for User on adapter ");
        $span = $this->startSpan("Count for User on adapter");
        try {
            $values = $this->conn->listForUpdate($filter, $sort);
            $last = end($values);
            return new UserSlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new UserCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(UserFilter $filter): ?User
    {
        $this->logDebug("Count for User on adapter ");
        $span = $this->startSpan("Count for User on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?UserFilter $filter): bool
    {
        $this->logDebug("Count for User on adapter ");
        $span = $this->startSpan("Count for User on adapter");
        try {
            return $this->conn->existsForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?UserFilter $filter = null): int
    {
        $this->logDebug("Count for User on adapter ");
        $span = $this->startSpan("Count for User on adapter");
        try {
            return $this->conn->countForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(User $entity, ?Closure $verify = null): User
    {
        $this->logDebug("Count for User on adapter ");
        $span = $this->startSpan("Count for User on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            $this->changelog->recordChange('user', $entity->uid(), $entity->asPublicJson(), []);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(UserRef $reference, User $entity): User
    {
        $this->logDebug("Count for User on adapter ");
        $span = $this->startSpan("Count for User on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            $original = ($reference instanceof User) ? $reference : $this->conn->retrieve(new UserFilter(uids: [ $reference->uid() ]));
            $this->changelog->recordChange('user', $entity->uid(), $entity->asPublicJson(), $original->asPublicJson());
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(User $entity): bool
    {
        $this->logDebug("Count for User on adapter ");
        $span = $this->startSpan("Count for User on adapter");
        try {
            $result = $this->conn->delete($entity);
            $this->dispach($entity);
            $this->changelog->recordDeletion('user', $entity->uid(), $entity->asPublicJson());
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByUid(string $uid): ?User
    {
        $this->logDebug("Find on by uid for User on adapter");
        $span = $this->startSpan("Find on by uid for User on adapter");
        try {
            return $this->conn->retrieveForUpdate(new UserFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByTenantAndName(TenantRef $tenant, string $name): ?User
    {
        $this->logDebug("Find on by tenant name for User on adapter");
        $span = $this->startSpan("Find on by tenant name for User on adapter");
        try {
            return $this->conn->retrieveForUpdate(new UserFilter(tenantAndName: UserFilter::tenantAndNameFilter(tenant: $tenant, name: $name)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(User $entity)
    {
        $this->logDebug("Count for User on adapter ");
        $span = $this->startSpan("Count for User on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
