<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\User\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use DateTime;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\Master\User\Application\Usecase\Update\UserUpdateUsecase;
use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserUidVO;
use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserTenantVO;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserNameVO;
use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserPasswordVO;
use Civi\Lughauth\Shared\Security\AesCypherService;
use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserEmailVO;
use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserTemporalPasswordVO;
use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserUseSecondFactorsVO;
use Civi\Lughauth\Features\Access\Master\User\Domain\ValueObject\UserVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\User\Application\Usecase\Update\UserUpdateParams;
use Civi\Lughauth\Features\Access\Master\User\Application\Usecase\Update\UserUpdateResult;

class UserUpdateController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly UserUpdateUsecase $updateUsecase,
        private readonly AesCypherService $cypherService,
    ) {
    }
    #[OA\Put(
        path: "/api/access/users/{uid}",
        tags: ["User"],
        description: "Update the value",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        requestBody: new OA\RequestBody(content: new OA\JsonContent(type: "object", ref:"#/components/schemas/UserApiDTO")),
        responses: [
        new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/UserApiDTO")),
        new OA\Response(response: 404, description: "Not found"),
        new OA\Response(response: 409, description: "Optimist lock"),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function update(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Update User");
        $span = $this->startSpan("Update User");
        $this->sql->begin();
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $value = $this->readUser($request);
            $result = $this->updateUsecase->update($uid, $value);
            $this->sql->commit();
            $value = $this->mapUser($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }

    private function readUser(ServerRequestInterface $request): UserUpdateParams
    {
        $this->logDebug("Read entity for User");
        $span = $this->startSpan("Read entity for User");
        try {
            $body = $request->getParsedBody();
            $errorsList = new ConstraintFailList();
            $value = new UserUpdateParams();
            $value->uid(UserUidVO::tryFrom($body['uid'] ?? null, $errorsList));
            if (in_array('tenant', array_keys($body))) {
                $value->tenant(UserTenantVO::tryFrom(isset($body['tenant']['$ref']) ? new TenantRef($body['tenant']['$ref']) : null, $errorsList));
            }
            $value->name(UserNameVO::tryFrom($body['name'] ?? null, $errorsList));
            $readPassword = $body['password'] ?? '******';
            if ($readPassword && '******' !== $readPassword) {
                $value->password(UserPasswordVO::tryFromPlainText($this->cypherService, $readPassword, $errorsList));
            }
            $value->email(UserEmailVO::tryFrom($body['email'] ?? null, $errorsList));
            $value->temporalPassword(UserTemporalPasswordVO::tryFrom($body['temporalPassword'] ?? null, $errorsList));
            $value->useSecondFactors(UserUseSecondFactorsVO::tryFrom($body['useSecondFactors'] ?? null, $errorsList));
            $value->version(UserVersionVO::tryFrom($body['version'] ?? null, $errorsList));
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $value;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function mapUser(UserUpdateResult $value): UserApiDTO
    {
        $this->logDebug("Map entity to output dto for User");
        $span = $this->startSpan("Map entity to output dto for User");
        try {
            $tenant = $value->getTenant();
            $dto = new UserApiDTO();
            $dto->uid = $value->getUid();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->name = $value->getName();
            $dto->password = '******';
            $dto->email = $value->getEmail();
            $dto->wellcomeAt = $value->getWellcomeAt()?->format(DateTime::ATOM);
            $dto->enabled = $value->isEnabled();
            $dto->approve = $value->getApprove();
            $dto->temporalPassword = $value->isTemporalPassword();
            $dto->useSecondFactors = $value->isUseSecondFactors();
            $dto->secondFactorSeed = '******';
            $dto->blockedUntil = $value->getBlockedUntil()?->format(DateTime::ATOM);
            $dto->provider = $value->getProvider();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
