<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\UserAcceptedTermnsOfUse\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\UserAcceptedTermnsOfUse\Domain\ValueObject\UserAcceptedTermnsOfUseUserVO;
use Civi\Lughauth\Features\Access\Master\User\Domain\UserRef;

trait UserAcceptedTermnsOfUseUserAccesor
{
    private UserAcceptedTermnsOfUseUserVO $_user;
    public function getUser(): UserRef
    {
        return $this->_user->value();
    }
    public function user(): UserAcceptedTermnsOfUseUserVO
    {
        return $this->_user;
    }
    public function isUserChanged(?self $original = null): bool
    {
        return $this->_user->value() !== ($original ? $original->_user->value() : null);
    }
    public function withUser(UserAcceptedTermnsOfUseUserVO|UserRef $user): self
    {
        $copy = clone $this;
        $copy->_user = UserAcceptedTermnsOfUseUserVO::from($user);
        return $copy;
    }
}
