<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\UserAccessTemporalCode\Domain\Gateway;

use Civi\Lughauth\Features\Access\Master\UserAccessTemporalCode\Domain\UserAccessTemporalCode;

class UserAccessTemporalCodeCursor
{
    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
    ) {
    }
    public function next(UserAccessTemporalCode $userAccessTemporalCode, ?int $size): UserAccessTemporalCodeCursor
    {
        $next = clone $this;
        $next->sinceUid = $userAccessTemporalCode->uid();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
}
