<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\UserIdentity\Application\Usecase\Create;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Application\Service\Visibility\UserIdentityVisibilityService;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\UserIdentity;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\Gateway\UserIdentityWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserIdentityCreateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserIdentityVisibilityService $visibility,
        private readonly UserIdentityWriteGateway $writer
    ) {
    }

    public function allowCreate(): Allow
    {
        $this->logDebug("Check allow of create usecase for User identity");
        $span = $this->startSpan("Check allow of create usecase for User identity");
        try {
            $result = $this->dispacher->dispatch(new UserIdentityCreateAllowDecision(Allow::allowed('create', 'Allowed to create by default')));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function create(UserIdentityCreateParams $params): UserIdentityCreateResult
    {
        $this->logDebug("Run create usecase for User identity");
        $span = $this->startSpan("Run create usecase for User identity");
        try {
            $allow = $this->allowCreate();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $this->dispacher->dispatch(new UserIdentityCreateCheck($params));
            $enriched = $this->dispacher->dispatch(new UserIdentityCreateEnrich($params, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $entity = UserIdentity::create($input);
            $result = $this->writer->create(
                $entity,
                fn ($created) =>
                            $this->visibility->checkVisibility($created)
            );
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new UserIdentityCreateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
