<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\Event;

use Override;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\UserIdentity;
use Civi\Lughauth\Shared\Event\PublicEvent;

class UserIdentityDeleteEvent extends UserIdentityEvent implements PublicEvent
{
    public function __construct(
        UserIdentity $payload
    ) {
        parent::__construct($payload);
    }
    #[Override]
    public function eventType(): string
    {
        return 'user-identity.delete';
    }
    #[Override]
    public function schemaVersion(): string
    {
        return 'v1';
    }
    #[Override]
    public function payload(): array
    {
        return [];
    }
    #[Override]
    public function original(): array
    {
        return $this->payload->asPublicJson();
        ;
    }
}
