<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\ValueObject\UserIdentityRelyingPartyVO;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\RelyingPartyRef;

trait UserIdentityRelyingPartyAccesor
{
    private ?UserIdentityRelyingPartyVO $_relyingParty = null;
    public function getRelyingParty(): ?RelyingPartyRef
    {
        return $this->_relyingParty?->value();
    }
    public function relyingParty(): ?UserIdentityRelyingPartyVO
    {
        return $this->_relyingParty;
    }
    public function isRelyingPartyChanged(?self $original = null): bool
    {
        return $this->_relyingParty->value() !== ($original ? $original->_relyingParty->value() : null);
    }
    public function withRelyingParty(UserIdentityRelyingPartyVO|RelyingPartyRef|null $relyingParty): self
    {
        $copy = clone $this;
        $copy->_relyingParty = UserIdentityRelyingPartyVO::from($relyingParty);
        return $copy;
    }
}
