<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\ValueObject\UserIdentityUserVO;
use Civi\Lughauth\Features\Access\Master\User\Domain\UserRef;

trait UserIdentityUserAccesor
{
    private UserIdentityUserVO $_user;
    public function getUser(): UserRef
    {
        return $this->_user->value();
    }
    public function user(): UserIdentityUserVO
    {
        return $this->_user;
    }
    public function isUserChanged(?self $original = null): bool
    {
        return $this->_user->value() !== ($original ? $original->_user->value() : null);
    }
    public function withUser(UserIdentityUserVO|UserRef $user): self
    {
        $copy = clone $this;
        $copy->_user = UserIdentityUserVO::from($user);
        return $copy;
    }
}
