<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\UserIdentity\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\UserIdentityRef;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Application\Usecase\Create\UserIdentityCreateUsecase;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Application\Usecase\List\UserIdentityListUsecase;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Application\Usecase\Update\UserIdentityUpdateUsecase;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Application\Usecase\Delete\UserIdentityDeleteUsecase;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Application\Usecase\Retrieve\UserIdentityRetrieveUsecase;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserIdentityAllowController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly UserIdentityCreateUsecase $createUsecase,
        private readonly UserIdentityListUsecase $listUsecase,
        private readonly UserIdentityUpdateUsecase $updateUsecase,
        private readonly UserIdentityDeleteUsecase $deleteUsecase,
        private readonly UserIdentityRetrieveUsecase $retrieveUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/me/acl/access/identity",
        tags: ["User identity access control"],
        description:"Allows",
        responses: [
        new OA\Response(response: 200, description: "Ok"),
    ]
    )
    ]
    public function genericAllow(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check generic allow for User identity");
        $span = $this->startSpan("Generic allow for User identity");
        try {
            $value = [
              'allows' => [
                $this->createUsecase->allowCreate(),
                $this->listUsecase->allowList(),
                $this->updateUsecase->allowUpdate(),
              $this->deleteUsecase->allowDelete(),
              $this->retrieveUsecase->allowRetrieve(),
              ],
              'fields' => [
                'noEditables' => $this->updateUsecase->fixedFields(),
                'noVisibles' => $this->retrieveUsecase->hiddenFields()
              ]
            ];
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[OA\Get(
        path: "/api/me/acl/access/identity/{uid}",
        tags: ["User identity access control"],
        description:"Allows",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok"),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function contextualAllow(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check specific allow for User identity");
        $span = $this->startSpan("Specific allow for User identity");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $ref = new UserIdentityRef(uid: $args['uid']);
            $value = [
              'allows' => [
                $this->updateUsecase->allowUpdate($ref),
              $this->deleteUsecase->allowDelete($ref),
              $this->retrieveUsecase->allowRetrieve($ref),
              ],
              'fields' => [
                'noEditables' => $this->updateUsecase->fixedFields($ref),
                'noVisibles' => $this->retrieveUsecase->hiddenFields($ref)
              ]
            ];
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
