<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Master\UserIdentity\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use OpenApi\Attributes as OA;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\Gateway\UserIdentityFilter;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\Gateway\UserIdentityCursor;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Domain\UserIdentityAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\Master\UserIdentity\Application\Usecase\List\UserIdentityListUsecase;
use Civi\Lughauth\Features\Access\Master\User\Domain\UserRef;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\Master\TrustedClient\Domain\TrustedClientRef;

class UserIdentityListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly UserIdentityListUsecase $listUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/access/identity",
        tags: ["User identity"],
        description:"Create",
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/UserIdentityApiListDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List User identity");
        $span = $this->startSpan("List User identity");
        try {
            $params = $request->getQueryParams();
            $filter = new UserIdentityFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                user: isset($params['user']) ? new UserRef($params['user']) : null,
                users: isset($params['users']) ? explode(",", $params['users']) : null,
                relyingParty: isset($params['relying-party']) ? new RelyingPartyRef($params['relying-party']) : null,
                relyingPartys: isset($params['relying-partys']) ? explode(",", $params['relying-partys']) : null,
                trustedClient: isset($params['trusted-client']) ? new TrustedClientRef($params['trusted-client']) : null,
                trustedClients: isset($params['trusted-clients']) ? explode(",", $params['trusted-clients']) : null,
            );
            $cursor = new UserIdentityCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapUserIdentity($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?UserIdentityCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for User identity");
        $span = $this->startSpan("Generic allow for User identity");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapUserIdentity(UserIdentityAttributes $value): UserIdentityApiDTO
    {
        $this->logDebug("Map entity to output dto for User identity");
        $span = $this->startSpan("Map entity to output dto for User identity");
        try {
            $user = $value->getUser();
            $relyingParty = $value->getRelyingParty();
            $trustedClient = $value->getTrustedClient();
            $dto = new UserIdentityApiDTO();
            $dto->uid = $value->getUid();
            $dto->user = $user ? ['$ref' => $user->uid()] : null;
            $dto->relyingParty = $relyingParty ? ['$ref' => $relyingParty->uid()] : null;
            $dto->trustedClient = $trustedClient ? ['$ref' => $trustedClient->uid()] : null;
            $roles = [];
            foreach ($value->getRoles() as $item) {
                $roles[] = [
                  'uid' => $item->uid(),
                  'role' => $item->getRole() ? ['$ref' => $item->getRole()->uid() ] : null,
                  'version' => $item->getVersion(),
                 ];
            }
            $dto->roles = $roles;
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
