<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Oidc\Client\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\Oidc\Client\Domain\ApiKeyData;
use Civi\Lughauth\Features\Access\Oidc\Client\Infrastructure\Driven\ApiKeyStoreAdapter;

class ApiKeyStoreGateway
{
    private readonly ApiKeyStoreRepository $repository;

    public function __construct(ContainerInterface $container, ?ApiKeyStoreRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(ApiKeyStoreAdapter::class);
    }
    public function apiKey(string $apiKey): ?ApiKeyData
    {
        return $this->repository->apiKey($apiKey);
    }
}
