<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Oidc\Key\Infrastructure\Driver\Rest;

use DateInterval;
use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Psr\SimpleCache\CacheInterface;
use Civi\Lughauth\Features\Access\Oidc\Key\Domain\KeysManagerService;

class JwksController
{
    public function __construct(private readonly KeysManagerService $tokenHandler, private readonly CacheInterface $cacheInterface)
    {
    }

    public function get(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $tenant = $args['tenant'];
        $key = $tenant . '_public_txt_jwks';
        if (!$this->cacheInterface->has($key)) {
            $data = $this->tokenHandler->keysAsJwks($tenant);
            $txt = json_encode($data);
            $this->cacheInterface->set($key, $txt, new DateInterval("PT1H"));
        } else {
            $txt = $this->cacheInterface->get($key);
        }
        $etag = '"' . md5($txt) . '"';
        $response->getBody()->write($txt);
        return $response
                    ->withHeader('Etag', $etag)
                    ->withHeader('Cache-Control', 'public, max-age=3600, immutable')
                    ->withHeader('Content-Type', 'application/json');
    }
}
