<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Oidc\User\Application\Usecase;

use Civi\Lughauth\Features\Access\Oidc\User\Domain\Gateway\RegisterUserGateway;

class RegisterUserUsecase
{
    public function __construct(private readonly RegisterUserGateway $gateway)
    {
    }

    public function allowRegister(string $tenant): bool
    {
        return $this->gateway->allowRegister($tenant);
    }

    public function getRegisterConsent(string $tenant): ?string
    {
        return $this->gateway->getRegisterConsent($tenant);
    }

    public function requestForRegister(string $url, string $tenant, string $email, string $password)
    {
        $this->gateway->requestForRegister($url, $tenant, $email, $password);
    }

    public function verifyRegister(string $tenant, string $code): ?string
    {
        return $this->gateway->verifyRegister($tenant, $code);
    }
}
