<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Oidc\User\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\Oidc\User\Infrastructure\Driven\ChangePasswordAdapter;

class ChangePasswordGateway
{
    private readonly ChangePasswordRepository $repository;
    public function __construct(ContainerInterface $container, ?ChangePasswordRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(ChangePasswordAdapter::class);
    }
    public function requestForChange(string $url, string $tenant, string $username): void
    {
        $this->repository->requestForChange($url, $tenant, $username);
    }
    public function allowRecover(string $tenant): bool
    {
        return $this->repository->allowRecover($tenant);
    }
    public function validateChangeRequest(string $tenant, string $code, string $newPass): ?string
    {
        return $this->repository->validateChangeRequest($tenant, $code, $newPass);
    }
    public function forceUpdatePassword(string $tenant, string $username, string $oldPass, string $newPass): bool
    {
        return $this->repository->forceUpdatePassword($tenant, $username, $oldPass, $newPass);
    }
}
