<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Application\Policy\Allow\Retrieve;

use Throwable;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Retrieve\RelyingPartyRetrieveAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class IsAutenticatedRetrieveAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(RelyingPartyRetrieveAllowDecision $proposal): RelyingPartyRetrieveAllowDecision
    {
        $this->logDebug("Check IsAutenticatedRetrieveAllow Relying party");
        $span = $this->startSpan("Check IsAutenticatedRetrieveAllow Relying party");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->allow->allowed) {
                $belongs = !$userContext->anonimous;
                $allowed = $belongs ? Allow::allowed('retrieve', 'Enabled for user IsAutenticatedRetrieve') : Allow::disallowed('retrieve', 'Disabled if not IsAutenticatedRetrieve');
                $proposal->allow = $allowed;
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
