<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Application\Policy\Allow\Update;

use Throwable;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Update\RelyingPartyUpdateAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UpdateRelyingPartyOnlyForRootAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(RelyingPartyUpdateAllowDecision $proposal): RelyingPartyUpdateAllowDecision
    {
        $this->logDebug("Check UpdateRelyingPartyOnlyForRootAllow Relying party");
        $span = $this->startSpan("Check UpdateRelyingPartyOnlyForRootAllow Relying party");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->isAllowed()) {
                if (! $userContext->hasAnyRole('root:admin')) {
                    $proposal->deny('Disabled if not UpdateRelyingPartyOnlyForRoot');
                }
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
