<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyAttributes;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingParty;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyFilter;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyCursor;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyReadGateway;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartySlide;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class RelyingPartyVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly RelyingPartyReadGateway $readGateway,
        private readonly RelyingPartyWriteGateway $writeGateway
    ) {
    }

    public function prepareVisibleDataInList(RelyingParty $content): RelyingPartyAttributes
    {
        return $this->prepareVisibleDataCallback($content, true);
    }
    public function prepareVisibleData(RelyingParty $content): RelyingPartyAttributes
    {
        return $this->prepareVisibleDataCallback($content, false);
    }
    public function copyWithFixed(RelyingPartyAttributes $attributes, ?RelyingParty $original = null): RelyingPartyAttributes
    {
        $this->logDebug("Copy with fixed for Relying party");
        $span = $this->startSpan("Copy with fixed for  Relying party");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new RelyingPartyPresetValues($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(RelyingPartyAttributes $attributes, ?RelyingParty $original = null): RelyingPartyAttributes
    {
        $this->logDebug("Copy with hidden for Relying party");
        $span = $this->startSpan("Copy with hidden for  Relying party");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|RelyingPartyRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Relying party");
        $span = $this->startSpan("Check visibility of an iterator for  Relying party");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new RelyingPartyFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, RelyingPartyRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new RelyingPartyRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(RelyingPartyFilter $filter, ?RelyingPartyCursor $cursor = null): RelyingPartySlide
    {
        $this->logDebug("List visibles for Relying party");
        $span = $this->startSpan("List visibles for  Relying party");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(RelyingPartyFilter $filter): int
    {
        $this->logDebug("Count visibles for Relying party");
        $span = $this->startSpan("Count visibles for  Relying party");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(RelyingPartyFilter $filter, ?RelyingPartyCursor $cursor = null): RelyingPartySlide
    {
        $this->logDebug("List visibles for update for Relying party");
        $span = $this->startSpan("List visibles for update for  Relying party");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(RelyingPartyFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Relying party");
        $span = $this->startSpan("Count visibles for update for  Relying party");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(RelyingPartyRef $ref): ?RelyingParty
    {
        $this->logDebug("Retrieve visible for Relying party");
        $span = $this->startSpan("Retrieve visible for  Relying party");
        try {
            $filter = new RelyingPartyFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(RelyingPartyRef $ref): ?RelyingParty
    {
        $this->logDebug("Retrieve visible for update for Relying party");
        $span = $this->startSpan("Retrieve visible for update for  Relying party");
        try {
            $filter = new RelyingPartyFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?RelyingPartyRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Relying party");
        $span = $this->startSpan("Check fields for fix for Relying party");
        try {
            $result = $this->dispacher->dispatch(new RelyingPartyCollectNonEditableFields(RelyingParty::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?RelyingPartyRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Relying party");
        $span = $this->startSpan("Check fields to hide for  Relying party");
        try {
            $result = $this->dispacher->dispatch(new RelyingPartyCollectNonVisibleFields([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(RelyingPartyAttributes $attributes): RelyingPartyAttributes
    {
        $this->logDebug("Check visibility of parent references for Relying party");
        $span = $this->startSpan("Check visibility of parent references for  Relying party");
        try {
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(RelyingPartyFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Relying party");
        $span = $this->startSpan("Compose visibility filter for  Relying party");
        try {
            $result = $this->dispacher->dispatch(new RelyingPartyRestrictFilterToVisibility($filter));
            return $result->relyingPartyFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(RelyingParty $value): bool
    {
        $this->logDebug("Check if item is visible for Relying party");
        $span = $this->startSpan("Check if item is visible for  Relying party");
        try {
            $result = $this->dispacher->dispatch(new RelyingPartyVisibilityCheck(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function prepareVisibleDataCallback(RelyingParty $content, bool $inlist): RelyingPartyAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for Relying party");
        $span = $this->startSpan("Prepare hidratation to visible data for Relying party");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new RelyingPartyEnrichForView($content, $inlist, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
