<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Service\Visibility\RelyingPartyVisibilityService;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyReadGateway;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyFilter;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyCursor;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class RelyingPartyListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly RelyingPartyVisibilityService $visibility,
        private readonly RelyingPartyReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for Relying party");
        $span = $this->startSpan("Check allow of list usecase for Relying party");
        try {
            $result = $this->dispacher->dispatch(new RelyingPartyListAllowProposal(Allow::allowed('list', 'Allowed to list by default')));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(RelyingPartyFilter $filter): int
    {
        $this->logDebug("Run list usecase for Relying party");
        $span = $this->startSpan("Run list usecase for Relying party");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new RelyingPartyListInputProposal($filter, null));
            return $this->visibility->countVisibles($input->filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(RelyingPartyFilter $filter, ?RelyingPartyCursor $cursor = null): RelyingPartyAttributesSlide
    {
        $this->logDebug("Run list usecase for Relying party");
        $span = $this->startSpan("Run list usecase for Relying party");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new RelyingPartyListInputProposal($filter, $cursor));
            $slide = $this->visibility->listVisibles($input->filter, $input->cursor);
            $output = new RelyingPartyAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($item->toAttributes()), $slide->values()));
            return $this->dispacher->dispatch(new RelyingPartyListOuputProposal($output))->slide;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
