<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyAttributes;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingParty;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Service\Visibility\RelyingPartyVisibilityService;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class RelyingPartyRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly RelyingPartyVisibilityService $visibility,
        private readonly RelyingPartyReadGateway $reader
    ) {
    }

    public function allowRetrieve(?RelyingPartyRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for Relying party");
        $span = $this->startSpan("Check allow of retrieve usecase for Relying party");
        try {
            $result = $this->dispacher->dispatch(new RelyingPartyRetrieveAllowProposal(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?RelyingPartyRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for Relying party");
        $span = $this->startSpan("Check hidden fields usecase for Relying party");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): RelyingPartyAttributes
    {
        $this->logDebug("Run retrieve usecase for Relying party");
        $span = $this->startSpan("Run retrieve usecase for Relying party");
        try {
            $ref = new RelyingPartyRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $input = $this->dispacher->dispatch(new RelyingPartyRetrieveInputProposal($result))->ref;
            $output = $this->visibility->copyWithHidden($input->toAttributes());
            return $this->dispacher->dispatch(new RelyingPartyRetrieveOutputProposal($output))->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
