<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyAttributes;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Service\Visibility\RelyingPartyVisibilityService;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class RelyingPartyUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly RelyingPartyVisibilityService $visibility,
        private readonly RelyingPartyWriteGateway $writer
    ) {
    }

    public function allowUpdate(?RelyingPartyRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Relying party");
        $span = $this->startSpan("Check allow update usecase for Relying party");
        try {
            $result = $this->dispacher->dispatch(new RelyingPartyUpdateAllowProposal(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?RelyingPartyRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Relying party");
        $span = $this->startSpan("Check fixed fields for update usecase for Relying party");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, RelyingPartyAttributes $attributes): RelyingPartyAttributes
    {
        $this->logDebug("Run update usecase for Relying party");
        $span = $this->startSpan("Run update usecase for Relying party");
        try {
            $ref = new RelyingPartyRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $input = $this->visibility->copyWithFixed($this->dispacher->dispatch(new RelyingPartyUpdateInputProposal($ref, $attributes))->attributes);
            $modified = $original->replace($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($result->toAttributes());
            return $this->dispacher->dispatch(new RelyingPartyUpdateOutputProposal($output))->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
