<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingParty;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driven\RelyingPartyReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;

class RelyingPartyReadGateway
{
    private RelyingPartyReadRepository $repository;
    public function __construct(ContainerInterface $container, ?RelyingPartyReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(RelyingPartyReadRepositoryAdapter::class);
    }
    public function enrich(RelyingPartyRef $ref): ?RelyingParty
    {
        return $this->repository->enrich($ref);
    }
    public function list(?RelyingPartyFilter $filter = null, ?RelyingPartyCursor $cursor = null): RelyingPartySlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(RelyingPartyFilter $filter): ?RelyingParty
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?RelyingPartyFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?RelyingPartyFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function findOneByUid(string $uid): ?RelyingParty
    {
        return $this->repository->findOneByUid($uid);
    }
    public function findOneByCode(string $code): ?RelyingParty
    {
        return $this->repository->findOneByCode($code);
    }
    public function findOneByApiKey(string $apiKey): ?RelyingParty
    {
        return $this->repository->findOneByApiKey($apiKey);
    }
}
