<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Closure;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingParty;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driven\RelyingPartyWriteRepositoryAdapter;

class RelyingPartyWriteGateway
{
    private RelyingPartyWriteRepository $repository;
    public function __construct(ContainerInterface $container, ?RelyingPartyWriteRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(RelyingPartyWriteRepositoryAdapter::class);
    }
    public function create(RelyingParty $entity, ?Closure $verify = null): RelyingParty
    {
        return $this->repository->create($entity, $verify);
    }
    public function update(RelyingPartyRef $ref, RelyingParty $entity): RelyingParty
    {
        return $this->repository->update($ref, $entity);
    }
    public function delete(RelyingParty $entity): bool
    {
        return $this->repository->delete($entity);
    }
    public function listForUpdate(?RelyingPartyFilter $filter = null, ?RelyingPartyCursor $cursor = null): RelyingPartySlide
    {
        return $this->repository->listForUpdate($filter, $cursor);
    }
    public function retrieveForUpdate(RelyingPartyFilter $filter): ?RelyingParty
    {
        return $this->repository->retrieveForUpdate($filter);
    }
    public function existsForUpdate(?RelyingPartyFilter $filter): bool
    {
        return $this->repository->existsForUpdate($filter);
    }
    public function countForUpdate(?RelyingPartyFilter $filter = null): int
    {
        return $this->repository->countForUpdate($filter);
    }
    public function findOneForUpdateByUid(string $uid): ?RelyingParty
    {
        return $this->repository->findOneForUpdateByUid($uid);
    }
    public function findOneForUpdateByCode(string $code): ?RelyingParty
    {
        return $this->repository->findOneForUpdateByCode($code);
    }
    public function findOneForUpdateByApiKey(string $apiKey): ?RelyingParty
    {
        return $this->repository->findOneForUpdateByApiKey($apiKey);
    }
}
