<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\RelyingPartyApiKeyVO;

trait RelyingPartyApiKeyAccesor
{
    private RelyingPartyApiKeyVO $_apiKey;
    public function getApiKey(): string
    {
        return $this->_apiKey->value();
    }
    public function apiKey(): RelyingPartyApiKeyVO
    {
        return $this->_apiKey;
    }
    public function isApiKeyChanged(?self $original = null): bool
    {
        return $this->_apiKey->value() !== ($original ? $original->_apiKey->value() : null);
    }
    public function withApiKey(RelyingPartyApiKeyVO|string $apiKey): self
    {
        $copy = clone $this;
        $copy->_apiKey = RelyingPartyApiKeyVO::from($apiKey);
        return $copy;
    }
}
