<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\RelyingPartyCodeVO;

trait RelyingPartyCodeAccesor
{
    private RelyingPartyCodeVO $_code;
    public function getCode(): string
    {
        return $this->_code->value();
    }
    public function code(): RelyingPartyCodeVO
    {
        return $this->_code;
    }
    public function isCodeChanged(?self $original = null): bool
    {
        return $this->_code->value() !== ($original ? $original->_code->value() : null);
    }
    public function withCode(RelyingPartyCodeVO|string $code): self
    {
        $copy = clone $this;
        $copy->_code = RelyingPartyCodeVO::from($code);
        return $copy;
    }
}
