<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\RelyingPartyEnabledVO;

trait RelyingPartyEnabledAccesor
{
    private ?RelyingPartyEnabledVO $_enabled = null;
    public function isEnabled(): ?bool
    {
        return $this->_enabled?->value();
    }
    public function enabled(): ?RelyingPartyEnabledVO
    {
        return $this->_enabled;
    }
    public function isEnabledChanged(?self $original = null): bool
    {
        return $this->_enabled->value() !== ($original ? $original->_enabled->value() : null);
    }
    public function withEnabled(RelyingPartyEnabledVO|bool|null $enabled): self
    {
        $copy = clone $this;
        $copy->_enabled = RelyingPartyEnabledVO::from($enabled);
        return $copy;
    }
}
