<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\RelyingPartyPoliciesVO;

trait RelyingPartyPoliciesAccesor
{
    private ?RelyingPartyPoliciesVO $_policies = null;
    public function getPolicies(): ?string
    {
        return $this->_policies?->value();
    }
    public function policies(): ?RelyingPartyPoliciesVO
    {
        return $this->_policies;
    }
    public function isPoliciesChanged(?self $original = null): bool
    {
        return $this->_policies->value() !== ($original ? $original->_policies->value() : null);
    }
    public function withPolicies(RelyingPartyPoliciesVO|string|null $policies): self
    {
        $copy = clone $this;
        $copy->_policies = RelyingPartyPoliciesVO::from($policies);
        return $copy;
    }
}
