<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\RelyingPartyScopesVO;

trait RelyingPartyScopesAccesor
{
    private RelyingPartyScopesVO $_scopes;
    public function getScopes(): string
    {
        return $this->_scopes->value();
    }
    public function scopes(): RelyingPartyScopesVO
    {
        return $this->_scopes;
    }
    public function isScopesChanged(?self $original = null): bool
    {
        return $this->_scopes->value() !== ($original ? $original->_scopes->value() : null);
    }
    public function withScopes(RelyingPartyScopesVO|string $scopes): self
    {
        $copy = clone $this;
        $copy->_scopes = RelyingPartyScopesVO::from($scopes);
        return $copy;
    }
}
