<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\RelyingParty\Domain\ValueObject\RelyingPartyVersionVO;

trait RelyingPartyVersionAccesor
{
    private ?RelyingPartyVersionVO $_version = null;
    public function getVersion(): ?int
    {
        return $this->_version?->value();
    }
    public function version(): ?RelyingPartyVersionVO
    {
        return $this->_version;
    }
    public function isVersionChanged(?self $original = null): bool
    {
        return $this->_version->value() !== ($original ? $original->_version->value() : null);
    }
    public function withVersion(RelyingPartyVersionVO|int|null $version): self
    {
        $copy = clone $this;
        $copy->_version = RelyingPartyVersionVO::from($version);
        return $copy;
    }
}
