            }
            $values = ['code' => $entity->getCode(), 'uid' => $entity->uid()];
            if ($this->db->exists('SELECT  "code" from "access_relying_party" where "code" = :code and "uid" != :uid', $values)) {
                throw ConstraintException::ofError('not-unique', ['code'], [$entity->getCode()]);
