            $values = ['apiKey' => $entity->getApiKey(), 'uid' => $entity->uid()];
            if ($this->db->exists('SELECT  "api_key" from "access_relying_party" where "api_key" = :apiKey and "uid" != :uid', $values)) {
                throw ConstraintException::ofError('not-unique', ['apiKey'], [$entity->getApiKey()]);
