<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyWriteRepository;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartySlide;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyCursor;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyFilter;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingParty;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\EntityChangeLog\EntityChangeLogService;

class RelyingPartyWriteRepositoryAdapter implements RelyingPartyWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly RelyingPartyPdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
        private readonly EntityChangeLogService $changelog,
    ) {
    }
    #[Override]
    public function resolveForUpdate(RelyingPartyRef $ref): ?RelyingParty
    {
        return $this->conn->retrieve(new RelyingPartyFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?RelyingPartyFilter $filter = null, ?RelyingPartyCursor $sort = null): RelyingPartySlide
    {
        $this->logDebug("Count for Relying party on adapter ");
        $span = $this->startSpan("Count for Relying party on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new RelyingPartySlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new RelyingPartyCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(RelyingPartyFilter $filter): ?RelyingParty
    {
        $this->logDebug("Count for Relying party on adapter ");
        $span = $this->startSpan("Count for Relying party on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?RelyingPartyFilter $filter): bool
    {
        $this->logDebug("Count for Relying party on adapter ");
        $span = $this->startSpan("Count for Relying party on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?RelyingPartyFilter $filter = null): int
    {
        $this->logDebug("Count for Relying party on adapter ");
        $span = $this->startSpan("Count for Relying party on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(RelyingParty $entity, ?Closure $verify = null): RelyingParty
    {
        $this->logDebug("Count for Relying party on adapter ");
        $span = $this->startSpan("Count for Relying party on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            $this->changelog->recordChange('relying-party', $entity->uid(), $entity->asPublicJson());
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(RelyingPartyRef $reference, RelyingParty $entity): RelyingParty
    {
        $this->logDebug("Count for Relying party on adapter ");
        $span = $this->startSpan("Count for Relying party on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            $this->changelog->recordChange('relying-party', $entity->uid(), $entity->asPublicJson());
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(RelyingParty $entity): bool
    {
        $this->logDebug("Count for Relying party on adapter ");
        $span = $this->startSpan("Count for Relying party on adapter");
        try {
            $result = $this->conn->delete($entity);
            $this->dispach($entity);
            $this->changelog->recordDeletion('relying-party', $entity->uid());
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByUid(string $uid): ?RelyingParty
    {
        $this->logDebug("Find on by uid for Relying party on adapter");
        $span = $this->startSpan("Find on by uid for Relying party on adapter");
        try {
            return $this->conn->retrieveForUpdate(new RelyingPartyFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByCode(string $code): ?RelyingParty
    {
        $this->logDebug("Find on by code for Relying party on adapter");
        $span = $this->startSpan("Find on by code for Relying party on adapter");
        try {
            return $this->conn->retrieveForUpdate(new RelyingPartyFilter(code: $code));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByApiKey(string $apiKey): ?RelyingParty
    {
        $this->logDebug("Find on by api key for Relying party on adapter");
        $span = $this->startSpan("Find on by api key for Relying party on adapter");
        try {
            return $this->conn->retrieveForUpdate(new RelyingPartyFilter(apiKey: $apiKey));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(RelyingParty $entity)
    {
        $this->logDebug("Count for Relying party on adapter ");
        $span = $this->startSpan("Count for Relying party on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
