<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver;

use Override;
use Slim\Routing\RouteCollectorProxy;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest\RelyingPartyAllowController;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest\RelyingPartyListController;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest\RelyingPartyCreateController;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest\RelyingPartyRetrieveController;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest\RelyingPartyUpdateController;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest\RelyingPartyDeleteController;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest\RelyingPartyEnableController;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest\RelyingPartyDisableController;
use Civi\Lughauth\Shared\Infrastructure\MicroPlugin;
use Civi\Lughauth\Shared\Event\EventListenersRegistrarInterface;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Service\Visibility\RelyingPartyFilterProposal;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Policy\Allow\Create\IsAutenticatedCreateAllow;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Create\RelyingPartyCreateAllowProposal;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Policy\Allow\Update\IsAutenticatedUpdateAllow;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Update\RelyingPartyUpdateAllowProposal;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Policy\Allow\Retrieve\IsAutenticatedRetrieveAllow;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Retrieve\RelyingPartyRetrieveAllowProposal;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Policy\Allow\List\IsAutenticatedListAllow;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\List\RelyingPartyListAllowProposal;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Policy\Allow\Delete\IsAutenticatedDeleteAllow;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Delete\RelyingPartyDeleteAllowProposal;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Policy\Allow\Enable\IsAutenticatedEnableAllow;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Enable\RelyingPartyEnableAllowProposal;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Policy\Allow\Disable\IsAutenticatedDisableAllow;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Disable\RelyingPartyDisableAllowProposal;

class RelyingPartyPlugin extends MicroPlugin
{
    #[Override]
    public function registerRoutes(RouteCollectorProxy $app)
    {
        $app->group('/api/access/relying-parties', [$this, 'setRoutesForRelyingParty']);
        $app->group('/api/me/acl/access/relying-parties', [$this, 'setRoutesForRelyingPartyAcl']);
    }
    #[Override]
    public function registerEvents(EventListenersRegistrarInterface $bus)
    {
        $bus->registerListener(RelyingPartyCreateAllowProposal::class, IsAutenticatedCreateAllow::class);
        $bus->registerListener(RelyingPartyUpdateAllowProposal::class, IsAutenticatedUpdateAllow::class);
        $bus->registerListener(RelyingPartyRetrieveAllowProposal::class, IsAutenticatedRetrieveAllow::class);
        $bus->registerListener(RelyingPartyListAllowProposal::class, IsAutenticatedListAllow::class);
        $bus->registerListener(RelyingPartyDeleteAllowProposal::class, IsAutenticatedDeleteAllow::class);
        $bus->registerListener(RelyingPartyEnableAllowProposal::class, IsAutenticatedEnableAllow::class);
        $bus->registerListener(RelyingPartyDisableAllowProposal::class, IsAutenticatedDisableAllow::class);
    }
    public function setRoutesForRelyingPartyAcl(RouteCollectorProxy $relyingPartyGroup)
    {
        $relyingPartyGroup->get('', [RelyingPartyAllowController::class, 'genericAllow']);
        $relyingPartyGroup->get('/{uid}', [RelyingPartyAllowController::class, 'contextualAllow']);
    }
    public function setRoutesForRelyingParty(RouteCollectorProxy $relyingPartyGroup)
    {
        $relyingPartyGroup->get('', [RelyingPartyListController::class, 'list']);
        $relyingPartyGroup->post('', [RelyingPartyCreateController::class, 'create']);
        $relyingPartyGroup->get('/{uid}', [RelyingPartyRetrieveController::class, 'retrieve']);
        $relyingPartyGroup->put('/{uid}', [RelyingPartyUpdateController::class, 'update']);
        $relyingPartyGroup->delete('/{uid}', [RelyingPartyDeleteController::class, 'delete']);
        $relyingPartyGroup->delete('', [RelyingPartyDeleteController::class, 'deleteAllForQuery']);
        $relyingPartyGroup->get('/~/delete-status/{uid}', [RelyingPartyDeleteController::class, 'checkDeleteAllForQueryStatus']);
        $relyingPartyGroup->patch('/~/enable', [RelyingPartyEnableController::class, 'enableAllForQuery']);
        $relyingPartyGroup->patch('/{uid}/enable', [RelyingPartyEnableController::class, 'enable']);
        $relyingPartyGroup->get('/~/enable-status/{uid}', [RelyingPartyEnableController::class, 'checkEnableAllForQueryStatus']);
        $relyingPartyGroup->patch('/~/disable', [RelyingPartyDisableController::class, 'disableAllForQuery']);
        $relyingPartyGroup->patch('/{uid}/disable', [RelyingPartyDisableController::class, 'disable']);
        $relyingPartyGroup->get('/~/disable-status/{uid}', [RelyingPartyDisableController::class, 'checkDisableAllForQueryStatus']);
    }
}
