<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Create\RelyingPartyCreateUsecase;

class RelyingPartyCreateController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly RelyingPartyCreateUsecase $createUsecase,
        private readonly RelyingPartyRestMapper $mapper,
    ) {
    }
    #[OA\Post(
        path: "/api/access/relying-parties",
        tags: ["Relying party"],
        description:"Create",
        requestBody: new OA\RequestBody(content: new OA\JsonContent(type: "object", ref:"#/components/schemas/RelyingPartyApiDTO")),
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/RelyingPartyApiDTO")),
              new OA\Response(response: 422, description: "Constraint error"),
          ]
    )]
    public function create(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Create Relying party");
        $span = $this->startSpan("Create Relying party");
        $this->sql->begin();
        try {
            $value = $this->mapper->readRelyingParty($request);
            $result = $this->createUsecase->create($value);
            $this->sql->commit();
            $value = $this->mapper->mapRelyingParty($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(201)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }
}
